import { useEffect, useState } from 'react';
import { Image, View, FlatList, Text, StyleSheet } from 'react-native';

function DetailCats({ navigation }) {
  
    useEffect(() => {
      getData();
    }, []);
  
    function getData() {
        fetch('https://straycat.herokuapp.com/Grey?id=' + route.params.msg)
          .then((response) => response.json())
          .then((json) => {
            setData(json);
          })
          .catch((error) => {
            console.error(error);
          });
      }

    function getData() {
      fetch('https://straycat.herokuapp.com/Brown?id=' + route.params.msg)
        .then((response) => response.json())
        .then((json) => {
          setData(json);
        })
        .catch((error) => {
          console.error(error);
        });
    }
  
    function getData() {
        fetch('https://straycat.herokuapp.com/Oren?id=' + route.params.msg)
          .then((response) => response.json())
          .then((json) => {
            setData(json);
          })
          .catch((error) => {
            console.error(error);
          });
      }

  function getData() {
    fetch('https://straycat.herokuapp.com/White?id=' + route.params.msg)
      .then((response) => response.json())
      .then((json) => {
        setData(json);
      })
      .catch((error) => {
        console.error(error);
      });
